<?phpif (!isset($_GET["sid"])) return;if (!isset($_GET["tid"])) return;$sid = $_GET["sid"];$tid = $_GET["tid"];$cn = mysqli_connect("localhost","loginid","loginpw") or die(mysqli_error());mysqli_select_db($cn,"dbname");$s = "select grade from grades where studid=".$sid." and itemid=".$tid;$r = mysqli_query($cn,$s);if (mysqli_num_rows($r)>0){	$a = mysqli_fetch_array($r);	print($a["grade"]);}else print("NULL");mysqli_close($cn);?>